/**
 * File: hjs-init.js
 * 
 * Copyright (c) 2018 Harmonic, Inc.
 * 
 * Handles construction and initialization of HJS, Harmonic Template,
 * and Harmonic Widgets.
 * 
 */

//------------------------------------------------------------------------------
// HJS Configuration
//------------------------------------------------------------------------------

/**
 * Global object that represents the HJS configuration.  
 * @type {Object}
 */
let hjsConfig = {
    severity: HJS.INFO,                    // Initial log severity
    remoteWebSocket : {                    // Connect to a remote websocket
        enable: false,                     // enable for connection
        ipAddress: "",                     // websocket server ip address
        port: 9500                         // websocket server port
    }
};

//------------------------------------------------------------------------------
// Harmonic JavaScript (aka HJS)
//------------------------------------------------------------------------------

/**
 * Global object that represents the HJS.  The HJS provides the interface to
 * and from the HTML renderer.
 * @type {HJS} 
 */
let hjs = new HJS(hjsConfig);

//------------------------------------------------------------------------------
// Harmonic Template and Widgets
//------------------------------------------------------------------------------

/**
 * Global object that provides the Harmonic Template functionality.  
 * @type {HarmonicTemplate}
 */
let harmonicTemplate = new HarmonicTemplate();

/**
 * Global object that contains the 2 Harmonic DVE objects.
 * @type {HarmonicDVE[]}
 */
let harmonicDVE = [ new HarmonicDVE(0), new HarmonicDVE(1) ];

/**
 * Global object that represents DVE 0 (standard/UHD channel).
 * @type {HarmonicDVE}
 */
let dve = harmonicDVE[0]; 

/**
 * Global object that represents DVE 0 (enhanced channel).
 * @type {HarmonicDVE}
 */
let dve0 = dve;

/**
 * Global object that represents DVE 1 (enhanced channel).
 * @type {HarmonicDVE}
 */
let dve1 = harmonicDVE[1];

/**
 * Global object that contains the 2 Harmonic Live AVO objects.
 * @type {HarmonicLiveAVO[]}
 */
let harmonicLiveAVO = [ new HarmonicLiveAVO(0), new HarmonicLiveAVO(1) ];

/**
 * Global object that represents Live AVO unit 0 (standard/UHD channel).
 * @type {HarmonicDVE}
 */
let liveAVO = harmonicLiveAVO[0]; 

/**
 * Global object that represents Live AVO unit 0 (enhanced channel).
 * @type {HarmonicDVE}
 */
let liveAVO0 = liveAVO;

/**
 * Global object that represents Live AVO unit 1 (enhanced channel).
 * @type {HarmonicDVE}
 */
let liveAVO1 = harmonicLiveAVO[1];

// Initialize HJS after creating the Harmonic Template and Widgets
hjs.init();

/**
 * File: hjs-test.js
 * 
 * Copyright (c) 2018 Harmonic, Inc.
 */   

 /**
  * Creates a global test object called 'hjstest'.  This object can be used
  * to inject websocket commands into the Harmonic JavaScript code.  This
  * functionality allows clients to test and debug issues.
  */
class HarmonicTemplateTest {

    constructor() {
        hjs.debug("HarmonicTemplateTest.constructor");
    }

    /**
     * Emulates the Oxtel Start Animation (S0) command.
     */
    startTemplateAnimation() {
        const obj = { 
            command: HJS.START_TEMPLATE_ANIMATION_COMMAND,
            tickid: 0 
        };

        hjs._onWebSocketMessage(JSON.stringify(obj));
    }

    /**
     * Emulates the Oxtel Stop Animation (S1) command.
     * @param {Boolean} immediate - false = complete cycle then stop, true = stop immediately
     */
    stopTemplateAnimation(immediate) {
        const obj = { 
            command: HJS.STOP_TEMPLATE_ANIMATION_COMMAND,
            tickid: 0,
            immediate: immediate 
        };                    

        hjs._onWebSocketMessage(JSON.stringify(obj));
    }

    /**
     * Emulates the Oxtel Select Animation Frame (S2) command.
     * @param {Number} frame 
     */
    gotoFrame(frame) {
        const obj = { 
            command: HJS.GOTO_FRAME_COMMAND,
            tickid: 0,
            frame: frame 
        };

        hjs._onWebSocketMessage(JSON.stringify(obj));
    }

    /**
     * Emulates the Oxtel Restart Animation (S4) command.
     */
    restartTemplateAnimation() {
        const obj = { 
            command: HJS.RESTART_TEMPLATE_ANIMATION_COMMAND,
            tickid: 0 
        };

        hjs._onWebSocketMessage(JSON.stringify(obj));
    }

    /**
     * Emulates the Oxtel Update Text Field (Z0) command 
     * with the Append flag not set.
     * @param {Number} fieldNum 
     * @param {String} text 
     * @param {Boolean} render - true = render now
     */
    updateTextField(fieldNum, text, render) {
        const obj = {
            command: HJS.UPDATE_TEXT_FIELD_COMMAND,
            tickid: 0,
            fieldNum: fieldNum,
            text: text,
            render: render
        };

        hjs._onWebSocketMessage(JSON.stringify(obj));
    }

    /**
     * Emulates the Oxtel Update Text Field (Z0) command 
     * with the Append flag set.
     * @param {Number} fieldNum 
     * @param {String} text 
     * @param {Boolean} render - true = render now
     */    
    appendTextField(fieldNum, text, render) {
        const obj = {
            command: HJS.APPEND_TEXT_FIELD_COMMAND,
            tickid: 0,
            fieldNum: fieldNum,
            text: text,
            render: render
        };

        hjs._onWebSocketMessage(JSON.stringify(obj));
    }

    /**
     * Emulates the Oxtel Render Box (Z3) command.
     * @param {Number} fieldNum 
     */
    renderField(fieldNum) {
        const obj = {
            command: HJS.RENDER_FIELD_COMMAND,
            tickid: 0,
            fieldNum: fieldNum
        };

        hjs._onWebSocketMessage(JSON.stringify(obj));
    }     

    /**
     * Emulates the Oxtel Render Box (Z3) command and
     * renders all text fields.
     */
    renderAllFields() {
        const obj = {
            command: HJS.RENDER_ALL_FIELDS_COMMAND,
            tickid: 0
        };

        hjs._onWebSocketMessage(JSON.stringify(obj));
    }         

    /**
     * Emulates the Oxtel Change Image (Z4) command.
     * @param {Number} fieldNum 
     * @param {String} imagePath 
     */
    updateImageField(fieldNum, imagePath) {
        const obj = {
            command: HJS.UPDATE_IMAGE_FIELD_COMMAND,
            tickid: 0,
            fieldNum: fieldNum,
            imagePath: imagePath
        };

        hjs._onWebSocketMessage(JSON.stringify(obj));
    }   

    /**
     * Emulates the Oxtel Stop Animation (Zf) command.
     * @param {Number} fieldNum 
     * @param {Boolean} immediate - false = complete cycle then stop, true = stop immediately
     */
    stopFieldAnimation(fieldNum, immediate) {
        const obj = {
            command: HJS.STOP_FIELD_ANIMATION_COMMAND,
            tickid: 0,
            fieldNum: fieldNum,
            immediate: immediate
        }
        hjs._onWebSocketMessage(JSON.stringify(obj));
    }

    /**
     * Emulates the Oxtel Pause/Restart Strap (Zg) command
     * @param {Number} fieldNum 
     * @param {Boolean} fromBeginning - true = start from beginning, false = start animation
     */
    restartFieldAnimation(fieldNum, fromBeginning) {
        const obj = {
            command: HJS.RESTART_FIELD_ANIMATION_COMMAND,
            tickid: 0,
            fieldNum: fieldNum,
            fromBeginning: fromBeginning
        }
        hjs._onWebSocketMessage(JSON.stringify(obj));
    }

    /**
     * Emulates the VISIBLE event.
     * @param {Number} alpha 
     */
    alphaNotZero(alpha=1) {
        const obj = {
            command: HJS.ALPHA_NOT_ZERO_COMMAND,
            tickid: 0,
            alpha: alpha
        }
        hjs._onWebSocketMessage(JSON.stringify(obj));
    }

    /**
     * Emulates the FULLY_VISIBLE event.
     * @param {Number} alpha 
     */
    alphaAtMax(alpha=1) {
        const obj = {
            command: HJS.ALPHA_AT_MAX_COMMAND,
            tickid: 0,
            alpha: alpha
        }
        hjs._onWebSocketMessage(JSON.stringify(obj));
    }

    /**
     * Emulates the transition from Preload to Load.
     * @param {Number} alpha 
     */
    activate(alpha) {
        const obj = {
            command: HJS.ACTIVATE_COMMAND,
            tickid: 0,
            alpha: alpha            
        }
        hjs._onWebSocketMessage(JSON.stringify(obj));
    }

    /**
     * Emulates the socket connected message.
     * @param {String} ipAddress 
     * @param {Number} port 
     * @param {Number} socketType 
     * @param {Number} socketId 
     */
    socketConnected(ipAddress, port, socketType, socketId) {
        const obj = {
            command: HJS.SOCKET_CONNECTED_COMMAND,
            ipAddress: ipAddress,
            port: port,
            socketType: socketType,
            socketId: socketId
        };
        hjs._onWebSocketMessage(JSON.stringify(obj));        
    }

    /**
     * Emulates the socket disconnected message.
     * @param {Number} socketId 
     */
    socketDisconnected(socketId) {
        const obj = {
            command: HJS.SOCKET_DISCONNECTED_COMMAND,
            socketId: socketId
        };
        hjs._onWebSocketMessage(JSON.stringify(obj));        
    }    

    /**
     * Emulates the socket data message.
     * @param {Number} socketId 
     * @param {String} data 
     */
    socketData(socketId, data) {
        const obj = {
            command: HJS.SOCKET_DATA_COMMAND,
            socketId: socketId,
            data: data
        };
        hjs._onWebSocketMessage(JSON.stringify(obj));        
    }    

    /**
     * Emulates the socket error message.
     * @param {String} ipAddress 
     * @param {Number} port
     * @param {Number} socketType 
     * @param {Number} socketId 
     * @param {Number} errorCode 
     */
    socketError(ipAddress, port, socketType, socketId, errorCode) {
        const obj = {
            command: HJS.SOCKET_ERROR_COMMAND,
            ipAddress: ipAddress,
            port: port,
            socketType: socketType,
            socketId: socketId,
            errorCode: errorCode
        };
        hjs._onWebSocketMessage(JSON.stringify(obj));        
    }

    ping() {
        const obj = {
            command: HJS.PING_COMMAND
        }
        hjs._onWebSocketMessage(JSON.stringify(obj));
    }
}

let hjstest = new HarmonicTemplateTest();

/**
 * File: hjs.js
 * 
 * Copyright (c) 2018 Harmonic, Inc.
 */

/**
 * This class provides the interface to/from the HTML renderer.  It uses a 
 * websocket connection to send and receive data from the HTML renderer.
 * 
 * This class provides the following capabilities:
 * - on platform vs. desktop detection
 * - log message functionality (to console and system log)
 * - individual interfaces for sending information to the HTML renderer
 * - dispatches COMMAND_RECEIVED_EVENT when valid data is received from 
 *   the HTML renderer
 */
class HJS {

    /**
     * Constructor 
     * - initializes all member variables.
     * 
     * @param {Object} config
     * @param {Number} config.severity;
     */
    constructor(config) {
        console.log("HJS.constructor");

        /**
         * The configuration used to construct HJS.  Reference {@link hjsConfig}
         * @type {Object} 
         */
        this._config = config;

        /**
         * The severity of the Log output.
         * @type {Number}
         */
        this._severity = config.severity;

        /**
         * A queue to log messages to before the websocket is ready.  Once
         * the websocket is opened, the severity will be sent followed by
         * the contents of the queue.
         * @type {Object[]}
         */
        this._queuedLogMsgs = [];

        /**
         * Flag indicating if the code is running on the platform or desktop.
         * @type {Boolean}
         */
        this._isOnPlatform = false;     

        /**
         * Flag indicating if the browser is running on the desktop and the
         * HTML renderer is running on the platform.  Used for debug purposes.
         * @type {Boolean}
         */
        this._isEmulatingPlatform = false;

        /**
         * IP address of the websocket server.
         * @type {String}
         */
        this._ipAddress = undefined;

        /**
         * Port of the websocket server.
         * @type {Number}
         */
        this._port = undefined;

        /**
         * The name of the loaded template.
         * @type {String}
         */
        this._templateName = undefined;

        /**
         * Flag indicating if the DVE SrcPreview should be visible.
         * @type {Boolean}
         */
        this._showDveSrcPreview = false;

        // init using the URL
        this._initFromURL();

        /**
         * The websocket to the HTML renderer.
         * @type {Object}
         */
        this._websocket = undefined;
    }

    /**
     * Initializes the websocket.  This must be called after the Harmonic
     * Template is constructed or it may miss the INIT_COMMAND from 
     * the HTML renderer.
     */
    init() {
        this.debug("HJS.init");
        this._initWebSocket();
    }

    /**
     * When running on the platform, the URL contains the ip address
     * and the port of the websocket to connect to.  Use this information
     * to determine if we are running on the platform vs. desktop and
     * to make the websocket connection.
     */
    _initFromURL() {

        let validParams = false;
        let urlParams = window.location.search.substring(1).split('?');
        let searchParams = new URLSearchParams(window.location.search);

        if (urlParams.length == 3) {
            // OLD HTML Renderer code support - should be removed over time.
            this._ipAddress = urlParams[0];
            this._port = urlParams[1];
            this._templateName = urlParams[2];   
            validParams = true;
        }
        else {
            // New HTML Renderer code support 
            // - key/value pairs for parameters
            this._ipAddress = searchParams.get("ip");
            this._port = searchParams.get("port");
            this._templateName = searchParams.get("template");

            if (searchParams.has("showDveSrcPreview")) {
                this._showDveSrcPreview = searchParams.get("showDveSrcPreview") === "1";
            }

            if ((this._ipAddress !== null) &&
                (this._port !== null) &&
                (this._templateName !== null))
            {
                validParams = true;
            }
        }

        if (validParams !== true) {
            this._ipAddress = undefined;
            this._port = undefined;
            this._templateName = window.location.pathname.substring(1);
        }

        // If the ip address and port are not specified in the URL, then
        // assume that we're running on the desktop.
        if ((this._ipAddress == undefined) || (this._port == undefined)) {        
            this._isOnPlatform = false;
            this.info("Running in a desktop browser");

            // Always show the DVE SrcPreview on the desktop.
            this._showDveSrcPreview = true;

            if (this._config.remoteWebSocket.enable) {
                this._ipAddress = this._config.remoteWebSocket.ipAddress;
                this._port = this._config.remoteWebSocket.port;
                this.info("Remote WebSocket configured: ipAddress: " + this._ipAddress + " port: " + this._port);

                this._isOnPlatform = true; 
                this._isEmulatingPlatform = true;
                this.info("Emulating platform behavior - isOnPlatform: " + this._isOnPlatform);
            }    
        }
        else {
            this._isOnPlatform = true;
            this.debug("Running on the platform");
        }        
    }

    /**
     * Initialize the websocket and add all event handlers local to this method.
     * 
     * @listens {'open'} - when the websocket connection is opened
     * @listens {'error'} - error on the websocket
     * @listens {'close'} - when the websocket connection closes
     * @listens {'message'} - when a message is received from the websocket
     */
    _initWebSocket() {        
        if (this.isOnPlatform()) {
            const url = "ws://" + this._ipAddress + ":" + this._port;
            this.debug("creating websocket: " + url);

            this._websocket = new WebSocket(url);

            //
            // Web socket Event Handlers
            //

            // Open
            this._websocket.addEventListener('open', (event) => {                
                this.debug("websocket: open");    

                // Set the severity on open so that JavaScript logged messages
                // can be seen in the log.
                this.setLogSeverity(this._severity);

                // Flush the log message queue.
                this._flushLogMsgQueue();
            });

            // Error
            this._websocket.addEventListener('error', (event) => {
                this.error("websocket error: " + event.target.url);
            });

            // Close
            this._websocket.addEventListener('close', (event) => {
                this.error("websocket: close");
            });

            // Message
            this._websocket.addEventListener('message', (event) => {
                this._onWebSocketMessage(event.data);
            });
        }
    }

    /**
     * Handles converting the websocket message to an object and dispatching
     * the event to the document.
     * 
     * @param {Object} data 
     */
    _onWebSocketMessage(data) {

        this.debug("websocket message: " + data);

        // Handle JSON
        try {
            const obj = JSON.parse(data);

            if (this._isObjectValid(obj)) {
                this._dispatchEvent(obj);
            }        
            else {
                this.error("websocket: invalid object from JSON: " + data);
            }
        } 
        catch(e) {
            this.error("websocket: failed to parse JSON: " + data);
        }
    }

